  getTranslation(key: string): string {
    return this.translationDictionary[key] || '';
  }


  navigateToSignup() {
    this.router.navigate(['/signup-form']);
  }

  navigateToForgotPassword() {
    this.router.navigate(['/forgot-password-form']);
  }
}

